#define NUM_SEGMENTS 128.0
#define NUM_POINTS (NUM_SEGMENTS * 2.0)
#define PI radians(180.)

#define NN 32.0

#define NP 8.0 
#define NSEG (vertexCount) 
//5.0

vec3 hsv2rgb(vec3 c) {
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

mat4 persp(float fov, float aspect, float zNear, float zFar) {
  float f = tan(PI * 0.5 - 0.5 * fov);
  float rangeInv = 1.0 / (zNear - zFar);

  return mat4(
    f / aspect, 0, 0, 0,
    0, f, 0, 0,
    0, 0, (zNear + zFar) * rangeInv, -1,
    0, 0, zNear * zFar * rangeInv * 2., 0);
}

void main() {

  float segCount = vertexCount / NP;

  float c0 = 0.25+(mod(vertexId, NP)/(NP));
  float count = mod(vertexId, NP);
  float point = 0.0;
  float ratio = 1.0;

  float t0 = mod( vertexId, NP);
  float t1 = mod(vertexId, 16.0);

  float u = floor(vertexId);
  float m0 = (mod(vertexId, vertexCount)-(t0))*0.25;
  float m1 = mod(m0, vertexCount);

  mat4 pr = persp(PI*0.25, resolution.x/resolution.y, 0.1, 100.0);

  gl_Position =  pr*(vec4(count, 
                      m0, 
                      mod(m1,NN), 
                      1.)+vec4(0.,0.,-50.,0.) );

  gl_PointSize = 5.0;

  v_color = vec4(c0, 1.0, 0.0, 0.);


}
