// from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
vec3 hsv2rgb(vec3 c)
{
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

#define PI radians(180.0);

void main() {
  float down = floor(sqrt(vertexCount));
  float across = floor(vertexCount / down);

  float x = mod(vertexId, across);
  float y = floor(vertexId / across);

  float u = x / (across - 1.);
  float v = y / (across - 1.);

  float xoff = 0.; //sin(time + y * 0.2) * 0.1;
  float yoff = 0.; //sin(time * 1. + x * 0.3) * 0.2;

  float ux = u * 2. - 1. + xoff;
  float vy = v * 2. - 1. + yoff;

  vec2 xy = vec2(ux, vy) * 1.3;

  float su = abs(u - 0.5) * 2.0;
  float sv = abs(v - 0.5) * 2.;
  float au = abs(atan(su, sv)) / PI;
  float av = length(vec2(su, sv));
  float snd = texture2D(sound, vec2(au * 0.05, av * 0.25)).a;

  gl_Position = vec4(xy, 0, 1);

  float soff = 0.; // sin(time * 1. + x * y * 0.02) * 15.; 

  gl_PointSize = pow(snd + 0.2, 5.) * 50.0 + soff;
  gl_PointSize *= 20. / across;
  gl_PointSize *= resolution.x / 600.;

  float pump = step(0.8, snd);
  float hue = u * .1 + snd; //sin(time * 1. + v * 20.) * 0.05;
  float sat = mix(0., 1., pump); // mix(0., 2., av);
  //float val = 1. - pow(av, 4.) * pow(snd + 0.2, 5.0); // sin(time * 1. + v * u * 20.0) * 0.5 + 0.5;
  float val = mix(0.1, pow(snd + 0.2, 5.0), pump);

  v_color = vec4(hsv2rgb(vec3(hue, sat, val)), 1);

}
