void main() {

  float snd = texture2D(sound, vec2(mouse.x)).a;
  float x = mod(vertexId, 10.);

  gl_Position = vec4(snd*mouse.x*x, 0, 0, 1);
  gl_PointSize = abs(sin(time))*snd*50.+20.;

  v_color = vec4(abs(sin(snd)),abs(cos(snd)),abs(tan(snd)),1.0);

}
