vec3 hsv2rgb(vec3 c)
{
	c = vec3(c.x,clamp(c.yz,0.0,1.0));
  vec4 k = vec4(1.0,2.0/3.0,1.0/3.0,3.0);
  vec3 p = abs(fract(c.xxx+k.xyz)* 6.0 - k.www);
  return c.z * mix(k.xxx,clamp(p-k.xxx,0.0,1.0),c.y);

}

#define PI radians(180.0)

void main() {
  float down = sqrt(vertexCount);
  float across = floor(vertexCount / down);

  float x = mod(vertexId, across);
  float y = floor(vertexId / across);

  float u = x / (across - 1.);
  float v = y / (across - 1.);

    float s = texture2D(sound, vec2(u,v)).a ;
  float xoff =  cos(time + y * 0.2) * 0.1;
  float yoff = sin(time + x * 0.3) * 0.2;

  float ux = u * 2. - 1. + xoff;
  float vy = v * 2. - 1. + yoff;

  vec2 xy = vec2(ux,vy) * 1.3;

  float su = abs(u - 0.5) * 2.;
  float sv = abs(v ) * 2.;
  float au = abs(atan(su,sv)) / PI;
  float av = length(vec2(su,sv));
  float snd = texture2D(sound, vec2(au * .05,av *.25)).a ;

  gl_Position = vec4(xy, 0, 1);

  float soff = sin(time + x* y* 0.02) * 5.;

  gl_PointSize = pow(snd+0.2,s + 5.) * 30.0 + soff;
  gl_PointSize *= 20.0 / across;
  gl_PointSize *= resolution.x / 2000.0;

  float pump = step(0.8,snd);
  float hue = u * .1 + snd * 0.2 + time * 0.1;//+ sin(time + v * 20.) * 0.05;
  float sat = mix(0.,1.,pump);
  float val = mix(.1,pow(snd + 0.2,5.),pump);// sin(time * 1.4 + v*u*20.0)*.5+.5;

  v_color = vec4(hsv2rgb(vec3(hue,sat,val)).xy,hue, 1);
}
