#define PI radians(180.0)
vec2 getCirclePoint(float id, float numcircle) {

//  float id = vertexId;

  float ux = floor(id/6.)+mod(id,2.);

  float vy = mod(floor(id/2.) + floor(id/3.),2.);

  float angle = ux / numcircle * PI * 2.;
  float c = cos(angle);
  float s = sin(angle);

  float radius =vy +1.0;

  float x =c * radius;
  float y = s * radius;

//  vec2 xy = vec2(x ,y);   
 // gl_Position = vec4(xy*0.1, 0, 1);  
 // v_color = vec4(1, 0, 0, 1);  

  return vec2(x, y);

}

void main() {

  float numcir = 20.;

  vec2 circleXY = getCirclePoint(vertexId, numcir);

  float numPointsPerCircle = numcir * 6.;
  float circleId = floor(vertexId/numPointsPerCircle);
  float numCircles = floor (vertexCount / numPointsPerCircle);


  float vcount = floor(sqrt(numCircles ));
  float across = floor(numCircles /vcount) ;

  float x = mod(circleId, across);
  float y = floor(circleId / across);



  float u = x / (across - 1.0);
  float v = y / (across - 1.0);

  float xoff = sin(time + y * 0.3) * 0.1;
  float yoff = sin(time + x * 0.3) * 0.2;

  float ux = u * 2.0 - 1.0 + xoff;
  float vx = v * 2.0 - 1.0 + yoff;

  vec2 xy =circleXY * 0.1 + vec2(ux ,vx) * 1.3;

  //float ux = u * 2.0 - 1.0 + xoff;
 // float vx = v * 2.0 - 1.0 + yoff;

//  vec2 xy = vec2(ux ,vx) * 0.7;

  gl_Position = vec4(xy, 0, 1);

  float soff = sin(time + x * y * 0.02 ) * 1.0;

  gl_PointSize = 1.0 + soff;
  gl_PointSize += 20. / across;
  gl_PointSize += resolution.x /600.;

  v_color = vec4(ux, vx, 0, 1);  
}
