void main(){
  float w = sqrt(vertexCount*fract(time));
  float h = 100.;//;floor(fract(time)*10.);
  float x = mod(vertexId, w);
  float y = floor(vertexId/h);
  float u = x/(w-1.);
  float v = y/(h-1.);
  float ux = u*2.-1.;
  float vy = v*2.-1.;
  gl_Position = vec4(ux,vy,0.,1.);
  gl_PointSize = mod(time,10.);
  v_color = vec4(1.);
}

