#define ITERS 50
#define M_PI 3.1415926535897932384626433832795

void main ()
{
  float ratio = resolution.x/resolution.y;
  float numAcrossDown = floor(sqrt(vertexCount));

  float maxVertexCount =   numAcrossDown* numAcrossDown;

  float finalVertexId = mod(vertexId, maxVertexCount);

  float x = mod(finalVertexId,  numAcrossDown);
  float y = floor(finalVertexId /  numAcrossDown);

  float u = x / numAcrossDown;
  float v = (y / numAcrossDown)*ratio;

  float ux = ( (u /*resolution.y/resolution.x*/) * 2.0 - 1.0) *resolution.y/resolution.x;
  float vy = ( (v*resolution.y/resolution.x) * 2.0 - 1.0);//*resolution.y/resolution.x;

  gl_Position = vec4(ux, vy, 0, 1);

  //gl_PointSize = ((resolution.y/numAcrossDown)*2.-1.) * 0.97;


  //logistic


  float snd = texture2D(sound, vec2(0.1,  ux)).a;
  float snd2 = texture2D(sound, vec2(0.1,  0.3)).a;
  //float snd3 = texture2D(sound, vec2(0.1,  0.5)).a;

  vec2 p = vec2(ux ,  vy);
  //float z = fract(0.86);
  float z = fract(1.2*snd);
  int a = 0;
  float trig = (cos(2. * M_PI * z+snd2) + 1.) / 2.;


  if(p.x>0.)p.x = -p.x;

  if(p.y<0.)p.y = -p.y;

  float coeff = mix(1., 3.75, trig) + p.x * mix(3., 0.25, trig);


  for (int i = 0; i < ITERS; i++) {
    a += (z > p.y && z <= p.y + 1. / numAcrossDown) ? 1 : 0;
    z = coeff * z * (1.-z);
  };

  float iters = float(ITERS);

  float g = 25. * float(a) / iters;


  gl_Position = vec4(ux, vy, 0, 1);

  gl_PointSize = 4.;//(resolution.y/numAcrossDown) * 1.;//(resolution.y/numAcrossDown)*2.-1.;

  v_color = vec4(g, g/8., g / 3.,1.);




  //v_color = vec4(1, 0, 0, 1);
}
