void main() {
  	float down = floor(sqrt(vertexCount)); // est grid
  	float across = floor(vertexCount / down); // more grid

  	float x = mod(vertexId, across); // red dot boi
  	float y = floor(vertexId / across); // twin


  	float u = x / (across -1.); 
  	float v = y / (across -1.);

  	float xoff = sin(time + y * 0.2) * 0.1;
  	float yoff = sin(time + x * 0.3) * 0.1;

  	float ux = u * 2. - 1. + xoff;
  	float vy = v * 2. - 1. + yoff;

  	vec2 xy = vec2(ux, vy) * 1.5;

  	gl_Position = vec4(xy, 0, 1);

  	float soff = sin(time + x * y * 0.02) * 5.;

  	gl_PointSize = 10.0 + soff;  
  	gl_PointSize *= 20. / across; 
  	gl_PointSize *= resolution.x / 600.;

  	v_color = vec4(1, 0, 0, 1);   // refers to R, G, B, alpha

}
