  	float ux = u * 2. - 1. + xoff;
  	float vy = v * 2. - 1. + yoff;

  	vec2 xy = vec2(ux, vy) * 1.3;

  	gl_Position = vec4(xy, 0, 1);

  	float soff = sin(time + x * y * 0.02) * 5.;

  	gl_PointSize = 15.0 + soff;
  	gl_PointSize *= 20.0 / across;
  	gl_PointSize *= resolution.x / 600.;

  	v_color = vec4(1, 0, 0, 1);
}"}
