#define PI radians(180.)
#define NUM_SEGMENTS 2.0
#define NUM_POINTS (NUM_SEGMENTS * 2.0)
#define STEP 1.0

void main() {

  vec2 p = vec2(0,0);

  if(vertexId < 0.5){
    p = vec2 (0., 0.5);
  }else if (vertexId < 1.5)
  {
     p = vec2 (0.5, -0.5);
  }else if (vertexId < 2.5)
  {
     p = vec2 (-0.5, -0.5);
  }

  gl_Position = vec4(p, 0., 1.);

  v_color = vec4(1,1,1, 1);
}
