#define ITERS 40
#define M_PI 3.1415926535897932384626433832795




void main ()
{
  //float snd = texture2D(sound, vec2(0.,  0.)).a;
  float finalVertexCount =  vertexCount;//max((0.5*snd)*vertexCount,5000.);
  float ratio = resolution.x/resolution.y;
  float numAcrossDown = floor(sqrt(finalVertexCount));

  float maxVertexCount =   numAcrossDown* numAcrossDown;

  float finalVertexId = mod(vertexId, maxVertexCount);

  float x = mod(finalVertexId,  numAcrossDown);
  float y = floor(finalVertexId /  numAcrossDown);

  float u = x / numAcrossDown;
  float v = (y / numAcrossDown)*ratio;

  float ux = ( u  * 2.0 - 1.0) *1./ratio;
  float vy = ( v/ratio * 2.0 - 1.0);



  float snd = texture2D(sound, vec2(0.,  0.)).a;
  //apply fragment logic

	//vec2 position = (gl_FragCoord.xy * 2.0 - resolution) / min(resolution.x, resolution.y);
	vec2 position = vec2(u, v);


  vec3 destColor = vec3(1.2, 5.0 * mouse.y, 0.6 * mouse.y);
	float f = 0.0;

	for(float i = 0.0; i < 20.0; i++)
	{
		float s = 0.2 * sin(0.11*(10.*snd+mouse.x*10.0) + i);
		float c = 1.2 * cos(0.02*(10.*snd+mouse.x*10.0) + i);
		f += 0.0015 / abs(length(11.0 * position * f - vec2(c, s)) - 0.8);
	}

	//gl_FragColor = vec4(vec3(destColor * f), 2);


  gl_Position = vec4(ux, vy, 0, 1);

  gl_PointSize = (resolution.y/numAcrossDown) * 1.;//(resolution.y/numAcrossDown)*2.-1.;

  v_color = vec4(vec3(destColor * f), 2);


}
