void main () {
  float v = vertexId;
  //float sndFactor = texture2D(sound, vec2(;
  float off = .1;

  float spiral = 1. - 1.7 * vertexId / (vertexCount+vertexCount);
  float scale = 1.;
  float grid =   floor(vertexCount / 1000.);
  float sndFactor = texture2D(sound, vec2(mod(v, 1.), mod(v, 240.))).a;


  float y = sin(v / 100.) * spiral ;//*sndFactor;
  float x = cos(v / 100. + sndFactor / 25.) * spiral;

  float ux = x * scale - off;
  float uy = y * scale - off;

  gl_Position  = vec4(ux, uy, 0, 1);
  gl_PointSize = 5.;//v / (sndFactor *100000.) ;// grid;
  //v_color = vec4(sin(spiral), sin(time/1.1), sin(time), 1);
  //v_color = vec4(sin(spiral * 5. +time + sndFactor), 0.1, 0.5, 1);
  v_color = vec4(sin(spiral * 10. +time + sndFactor), 0.1, 0.5, 1);
}
