#define PI 3.14159
#define TAU (PI * 2.0)


vec3 hsv2rgb(vec3 c) {
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 rotate(float freq, float phase, float radius, float index)
{
	float angle = (index * freq + phase) * TAU;
	return vec2(radius * cos(angle), radius * sin(angle));
}

float oscilate(float freq, float phase, float amp, float index)
{
  return amp * sin((index * freq + phase) * TAU);
}

void main() {
  	float index = vertexId / vertexCount;
  	float count = floor(index);


  float r1_freq = 20.0;
  float r1_phase = .30;
  float r1_radius = .70;
  float r1_osc_freq = 10.0;
  float r1_osc_phase = 0.0;
  float r1_osc_amp = 0.1;


  float r2_freq = 556.0;
  float r2_phase = .50;
  float r2_radius = .90;  
  float r2_osc_freq = 20.0;
  float r2_osc_phase = 0.0;
  float r2_osc_amp = 0.3;

  vec2 xy = rotate( r1_freq, r1_phase + time*0.001, 
                   r1_radius + oscilate(r1_osc_freq, 
                                        r1_osc_phase,
                                        r1_osc_amp,
                                        index), index) + 
    		rotate( r2_freq, r2_phase + time *0.01, 
                   r2_radius + oscilate(r2_osc_freq, 
                                        r2_osc_phase, 
                                        r2_osc_amp,
                                        index), index);

  //xy =  vec2 (index,index);

  vec2 aspect = vec2(resolution.y / resolution.x, 1);

  gl_Position = vec4(xy * aspect, xy.y, 2);


  float hue = 0.025;
