void main()
{
  float vertexPerCircle = 42.0;
  float circlePerRow = 22.0;

  float x = floor(vertexId / 2.0);
  float y = mod(vertexId + 1.0, 2.0);

  float xRow = floor(mod(vertexId , vertexPerCircle * circlePerRow) / vertexPerCircle);
  float yColumn = floor(vertexId / (vertexPerCircle * circlePerRow));

  //float x = floor(vertexId / 2.0);
  //float y = mod(vertexId + 1.0, 2.0);

  // Radianes de 360 da pi
  float angle = x / 20.0 * radians(360.0);
  float radius = 2.0 - y;

  // Movimiento
  float xOffset = cos(time + yColumn * 0.2) * 0.4;
  float yOffset = cos(time + xRow * 0.2 ) * 0.8;

  // Se asignan a 4 cuadrantes
  float u = radius * cos(angle) * 0.2 + xOffset;
  float v = radius * sin(angle) * 0.2 + yOffset;

  vec2 xy = vec2(u + xRow , v + yColumn) * 0.1 - 1.0;

  gl_Position = vec4(xy, 0.0, 1.0);
  gl_PointSize = 1.0;
  v_color = vec4(0.0, 0.7, 1.0, 1.0);
}
