{"num":1000,"mode":"POINTS","sound":"","lineSize":"NATIVE","backgroundColor":[0.12941176470588237,0.1411764705882353,0.19215686274509805,1],"shader":"vec3 hsv2rgb(vec3 c)
{
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void main() {

  float c = vertexCount;
  float down = floor(sqrt(c));
  float across = floor(c/down);

  float x = mod(vertexId, across);
  float y = floor(vertexId / across);

  float u = x/(across-1.);
  float v = y/(across-1.);

  float xoff = sin(time + y*0.2) * 0.1;
  float yoff = sin(time*1.1 + x*0.3) * 0.2;

  float ux = u*2.-1. + xoff;
  float vy = v*2.-1. + yoff;

  vec2 xy = vec2(ux,vy) * 1.5;

  gl_Position = vec4(xy, 0.,1.);

  float soff = sin(time*1.2 + x*y*.02) * 5.;

  gl_PointSize = 15. + soff;
  gl_PointSize *= 20./across;
  gl_PointSize *= resolution.x/600.;

  float hue = u*.1 + sin(time*1.3 + v*20.)*.05;
  float sat = 1.;
  float val = sin(time*1.4 + v*u*20.)*.5+.5;

  v_color = vec4(hsv2rgb(vec3(hue,sat,val)), 1.);

}"}
