settings.shader:
//Step 1 >> comment all precision, extension, uniforms and varying
/*
#ifdef GL_ES
precision mediump float;
#endif

#extension GL_OES_standard_derivatives : enable

uniform float time;
uniform vec2 mouse;
uniform vec2 resolution;
varying vec2 surfacePosition;
*/

#define seedFactor 0.75//KParameter 0.2>>2.
#define timeFactor 0.1//KParameter 0.1>>10.


#define MAX_ITER 64

vec2 fractal(in vec2 p, in vec2 i) {
	return vec2(dot(i, vec2(p.x, -p.y)), dot(p, i.yx));	
}

vec3 color(in float c) {
	return vec3(
		c*sin(c*0.33),
		c*sin(c*0.66),
		c*sin(c)
	);
}

void main( void ) {


  //Step 2 >> replace  gl_FragCoord
  float finalDesiredPointSize = 3.;
  float maxFinalSquareSideSize = floor(sqrt(vertexCount));
  float finalMaxVertexCount = maxFinalSquareSideSize*maxFinalSquareSideSize;

  float maxVerticesPerLine = floor(maxFinalSquareSideSize  *resolution.x/resolution.y);

  //x = number of elements in a line as x value for local resolution
  //y = number of possible lines with the given vertexCount
  vec2 vertexResolution = vec2(maxVerticesPerLine, floor(finalMaxVertexCount/maxVerticesPerLine));

  //we can now calculate the final number of elements
  float finalVertexCount = vertexResolution.x*vertexResolution.y;

  //and adjust finalVertexId
  float finalVertexId = mod(vertexId,finalVertexCount);

  //Calculate the position of the elements based on their finalVertexId
  float x = mod(finalVertexId, vertexResolution.x);
  float y = floor(finalVertexId / vertexResolution.x);

  vec2 simFragCoord = vec2(x,y);//simfragCoord will replace the gl_FragCoord of fragment shader


  //Step 3 >> calculate vertice positions
  //relative coordinate of the vertex (cordinates in 0..1 referential)
  float u = (x /vertexResolution.x);
  float v = (y /vertexResolution.y);

  //set vertices disposition
  float sizeFactor = finalDesiredPointSize*vertexResolution.x/resolution.x;

  float ux = sizeFactor*(u - 0.5);
  float vy = sizeFactor*(v - 0.5);

  gl_PointSize = finalDesiredPointSize;
  gl_Position = vec4(ux, vy, 0., 1.);

  //create the surfacePosition
  vec2 surfacePosition = vec2(ux,vy);

   /****************************************************************/
  //Step 4 >> paste the old fragment code with following changes:
  //replace gl_FragCoord by simFragCoord 
  //replace gl_FragColor by v_color

	vec2 p = (( simFragCoord.xy / vertexResolution ) - 0.5) * 2.0;
	p.x *= resolution.x/resolution.y;

	float angle = time * timeFactor;
	mat2 rot = mat2(cos(angle), -sin(angle),
		  sin(angle), cos(angle));
	p *= rot;
	vec2 seed = vec2(sin(angle)*.001, 1.0-cos(1.0/time));

	vec2 i = p;


	float c = 1.0;
	for (int n = 0; n < MAX_ITER; n++) {
		vec2 r = i;
		i = fractal(i,r) + seed;
		i = fractal(i,r) + seed.yx;
		i = fractal(i,r) + r;

		c += length(r);
		if (length(i) > 10.0) {
			break;
		}
	}

	c /= float(MAX_ITER);
	v_color = vec4( vec3( color(pow(dot(c+c+sin(time)*3., 2.0), 2.2)) ), 1.0 );//crazier colors




  /****************************************************************/

}
