#define PI 3.141

float dis(float n) 
{ 
  return fract(sin(n)*8.700); 
}



void main () 
{
  float Id = dis(vertexId);
  float fr = dis(Id);
  float tex = texture2D(sound, vec2(fr, Id)).a * cos(Id);

  tex = pow(tex, 2.9);

  float pang = vertexId;
  float view = 7.5 * 2.0;
  float t = (time * (fr + .1))*10.6;
  float x = Id * sin(pang + t);
  float y = Id * cos(pang + t);



  y +=  52.25 * tex * (.12 - y);
  y *= 87.78;



  float sizeAfter = tex / (y + 1.15);

  gl_Position = vec4(x, y, 0.4, 1.3);
  gl_PointSize = 6.2 * sizeAfter;

  v_color = vec4(tex * 1.1 * (3. - fr), tex * 0.9 * cos(fr * PI), tex * 9.10, sizeAfter);

}
