settings.shader:
vec3 hsv2rgb(vec3 c) {

  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);

}

#define PI radians(180.0)

void main() {

  float down = floor(sqrt(vertexCount));

  float across = floor(vertexCount / down);



  float x = mod(vertexId, across);
  float y = floor(vertexId / across);


  float u = x / (across - 1.0);
  float v = y / (across - 1.0);

  float xoff = sin(time + y * 0.2) * 0.1;
  float yoff = sin(time + x * 0.1) * 0.25;

  float ux = u * 2.0 - 1.0 + xoff;
  float vy = v * 2.0 - 1.0 + yoff;

  vec2 xy = vec2(ux, vy) * 1.0; // x,y pos and scale;


  float su = abs(u - 0.5) * 2.0;
  float sv = abs(v - 0.5) * 2.0;

  float au = abs(atan(su, sv)) / PI;
  float av = length(vec2(su, sv));

  float snd = texture2D(sound, vec2(au * 0.05, av * 0.25)).a;

  gl_Position = vec4(xy, 0.0, 1.0);


  float soff = sin(time + x * y * 0.02) * 8.0;

  gl_PointSize = pow(snd + 0.3, 5.0) * 20.0 + soff;
  gl_PointSize *= 20.0 / across;
  gl_PointSize *= resolution.x / 600.0;


  float pump = step(0.2, snd);

  float hue = u + 0.1 + snd * 0.2 + time * 0.1;  // u * 0.1 + sin(time + v * 20.0) * 0.05;
  float saturation = mix(0.0, 1.0, pump);
  float value = mix(0.1, pow(snd + 0.2, 5.0), pump); //sin(time + v * u * 20.0) * 0.5 + 0.5;


  v_color = vec4(hsv2rgb(vec3(hue, saturation, value)), 1.0);
}
