settings.shader:
#define PI 3.14159
#define NUM_SEGMENTS 4.0
#define NUM_POINTS (NUM_SEGMENTS * 2.0)
#define STEP 5.0
//#define FIT_VERTICAL
vec3 posf2(float i) {
	return vec3(
      sin(i*.9553) +
      sin(i) +
      sin(i*1.53) +
      sin(i*.76),
      sin(i*.79553+2.1) +
      sin(i*1.1311+2.1) +
      sin(i*1.353-2.1) +
      sin(i*.476-2.1),
      sin(i*.5553-2.1) +
      sin(i*1.1-2.1) +
      sin(i*1.23+2.1) +
      sin(i*.36+2.1)
	)*.2;
}
vec3 posf2d(float i) {
	return vec3(
      cos(i*.9553)*.9553 +
      cos(i) +
      cos(i*1.53)*1.53 +
      cos(i*.76)*.76,
      cos(i*.79553+2.1)*.79553 +
      cos(i*1.1311+2.1)*1.1311 +
      cos(i*1.353-2.1)*1.353 +
      cos(i*.476-2.1)*.476,
      cos(i*.5553-2.1)*.5553 +
      cos(i*1.1-2.1)*1.1 +
      cos(i*1.23+2.1)*1.23 +
      cos(i*.36+2.1)*.36
	)*.2;
}

vec3 hsv2rgb(vec3 c) {
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void main() {
  vec3 pos = posf2(vertexId*.002);
  vec3 posd = posf2d(vertexId*.002);

  #ifdef FIT_VERTICAL
    vec2 aspect = vec2(resolution.y / resolution.x, 1);
  #else
    vec2 aspect = vec2(1, resolution.x / resolution.y);
  #endif

  float t = time*.2;
  mat2 m = mat2(cos(t),-sin(t),sin(t),cos(t));
  t *= 1.31;
  mat2 m2 = mat2(cos(t),-sin(t),sin(t),cos(t));
  pos.yz *= m;
  pos.zx *= m2;
  posd.yz *= m;
  posd.zx *= m2;

  pos.z += .7;
  float blurDist = .7;

  float size = .5/pos.z;
  float blur = 20.*abs(1./pos.z-1./blurDist);


  gl_Position = vec4(pos.xy/pos.z * aspect, 1.-vertexId*.000001, 1);
  gl_PointSize = size+blur;

  vec3 colour = abs(normalize(posd));

  v_color = vec4(size/(size+blur)*size/(size+blur))*length(posd)*2.*vec4(colour,1.);
}
