{"num":1,"mode":"POINTS","sound":"https://soundcloud.com/tonycuozzo/space-song-beach-house-slowed","lineSize":"NATIVE","backgroundColor":[0,0,0,1],"shader":"vec3 hsv2rgb(vec3 c) {
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void main() {
  float down = floor(sqrt(vertexCount));
  float across = floor(vertexCount / down);

  float x = mod(vertexId, across);
  float y = floor(vertexId / across);

  float u = x / (across - 1.);
  float v = y / (across - 1.);

  float xoff = sin(time * 1.2 + y * 0.1) * 0.1;
  float yoff = sin(time * 1.1 + x * 0.2) * 0.2;

  float ux = u * 2. - 1. + xoff;
  float vy = v * 2. - 1. + yoff;

  vec2 xy = vec2(ux, vy) * 1.2; // stretches grid past 1.

  float sv = abs(v - 0.5) * 2.;
  float su = abs(u - 0.5) * 2.;
  float sound = texture2D(sound, vec2(su * .05, sv + .25)).a;

  gl_Position = vec4(xy, 0, 1);

  float soff = sin(time + x * y * 0.02) * 5.;

  gl_PointSize = pow(sound + 0.2, 5.) * 7. + soff;
  gl_PointSize *= 20. / across;
  gl_PointSize *= resolution.x / 600.;

  float hue = u * .1 + sin(time + v * 10.) * 0.15;
  float sat = 1.;
  float val = sound * sin(time + v * u * 20.0) * .6 + .8;

  v_color = vec4(hsv2rgb(vec3(hue, sat, val)), 1);

}"}
