void main() {

  vec2 xy = vec2(0.0,0.0);


  if (vertexId < 2.0) {
  	xy = vec2(vertexId - 0.5,0.0);
  } else {
    xy = vec2(vertexId/100.0 - 0.5, mod(vertexId, 2.0) - 0.5 );

    float s = texture2D(sound, (xy+ vec2(0.5,0.5))*0.2).a;
    xy.y +=s;

  }



  gl_Position = vec4(xy * 0.5, 0, 1);

  v_color = vec4(1.0,1.0,1.0,1.0)
}
