settings.shader:
vec3 hsv2rgb(vec3 c) {
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void main() {
  float down = floor(sqrt(vertexCount));
  float across = floor(vertexCount / down);

  float x = mod(vertexId, across);
  float y = floor(vertexId / across);

  float u = x / (across - 1.);
  float v = y / (across - 1.);

  float xoff = sin(time + y * .2) * .02;
  float yoff = sin(time + x * .2) * .02;


  float ux = u * 2. - 1. + xoff;
  float vy = v * 2. - 1. + yoff;

  float scalar = .0;
  float scale = scalar + (resolution.y + resolution.x) / 400.;

  vec2 xy = vec2(ux, vy) * scale;
  float z = clamp(sin(time), 0.5 , 1.);

  gl_Position = vec4(xy, z, 1);

  float soff = sin(time + xy.x * xy.y * .002) * 15.;

  gl_PointSize = 10. + soff;
  gl_PointSize *= 10. / across;
  gl_PointSize *= scale;


  float hue = u;
  float sat = 1.;
  float val = 1.;

  v_color = vec4(hsv2rgb(vec3(hue, sat, val)), 1);
}
