void main()
{
  //vertexId is 0,1,2,3,4 .....
  //By dividing by 10, it's going to be 0.0, 0.1, 0.2 , 0.3.....
  float value = vertexId/10.;

  //We can get a bunch of form by sending mouse position * value
  gl_Position = vec4(mouse.x*value  , mouse.y*value , 0,1);
  //By using sin we can get loop of sin and the size will be from 0 to 50.
  gl_PointSize = abs(sin(time+value))*50.;

  //Set with sin, cos, tan to get various color. 
  v_color = vec4(sin(time), cos(time), tan(time), 1);
}
