const int maxIter = 50;
float df(vec3 p, float power) {
    vec3 z = p;
    float r = 0.0;
    float dr = 1.0;
    for(int i = 0; i < maxIter; i++) {
        r = length(z);
        if(r > 100.0) break;

        float theta = acos(z.z/r);
        float phi = atan(z.y, z.x);

        dr = power*pow(r, power-1.0)*dr + 1.0;

        r = pow(r, power);
        theta *= power;
        phi *= power;

        z = r*vec3(sin(theta)*cos(phi), sin(theta)*sin(phi), cos(theta));
        z += p;
    }
    return 0.5*log(r)*r/dr;
}


struct Ray {
    bool hit;
    vec3 hitPos;
    float t;
    int steps;
};
const int maxSteps = 50;
Ray trace(vec3 camPos, vec3 rayDir) {
    vec3 p = vec3(0);
    float t = 0.0; 
    int steps = 0;
    bool hit = false;
    for(int i = 0; i < maxSteps; i++) {
        p = camPos + t*rayDir;
        float d = df(p, 8.0*abs(sin(0.5*time)) + 1.0);
        if(d < 0.001) {
            hit = true;
            steps = i;
            break;
        }
        t += d*0.9;
    }
    return Ray(hit, p, t, steps);
}


vec3 shading(Ray tr) {
    if(tr.hit) {
    	return vec3(pow(1.0 - float(tr.steps)/float(maxSteps), 0.7));
    }
    else {
        return vec3(0);
    }
}




void main ()
{
   float finalDesiredPointSize = 3.;
   float maxFinalSquareSideSize = floor(sqrt(vertexCount));
   float finalMaxVertexCount = maxFinalSquareSideSize*maxFinalSquareSideSize;

  //first the number of elements in a line
  //we want to keep the resolution.xresolution.y ratio
  float across = floor(maxFinalSquareSideSize  *resolution.x/resolution.y);

  //deduce from finalMaxVertexCount the number of possible lines with the given across
  float down = floor(finalMaxVertexCount / across);

  //Now we have the across and down number of elements, we can now calculate the final number of vertices
  float finalVertexCount = across*down;

  //and the consequent finalVertexId
  float finalVertexId = mod(vertexId,finalVertexCount);

  //Now we calculate the position of the elements based on their finalVertexId
  float x = mod(finalVertexId, across);
  float y = floor(finalVertexId / across);

  float u = (x /across);
  float v = (y /down);

  float u0 = (u * (across*finalDesiredPointSize/resolution.x));
  float v0 = (v * (across*finalDesiredPointSize/resolution.x ));

  float ux =  u0 - 0.5*(across*finalDesiredPointSize/resolution.x);
  float vy =  v0- 0.5*(across*finalDesiredPointSize/resolution.x);;



  //vec2 fragCoord = vec2(ux,vy);

    //apply fragment logic

	//vec2 fragCoord = vec2(ux,vy);

	vec2 iResolution = vec2(across, down);
    float iTime = time;

	//vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;

     vec2 uv = vec2(u,v);

    //vec3 camPos = vec3(-2, 0, 0);
    vec3 camPos = 2.0*vec3(cos(iTime), 0, sin(iTime));
    vec3 camFront = normalize(-camPos);
    vec3 camUp = vec3(0, 1, 0);
    vec3 camRight = cross(camFront, camUp);
    float focus = 1.0;

    vec3 rayDir = normalize(uv.x*camRight + uv.y*camUp + focus*camFront);
    Ray tr = trace(camPos, rayDir);

	//fragColor = vec4(shading(tr), 1.0);


  gl_Position = vec4(ux, vy, 0, 1);

  gl_PointSize = finalDesiredPointSize;

  v_color = vec4(shading(tr), 1.0);

  //v_color = vec4(1.,1.,1.,1.);
}
