    float maxFinalSquareSideSize = floor(sqrt(vertexCount));
  float finalMaxVertexCount = pow(maxFinalSquareSideSize,2.);

  float maxVerticesPerLine = floor(maxFinalSquareSideSize  *resolution.x/resolution.y);

  //x = number of elements in a line as x value for local resolution
  //y = number of possible lines with the given vertexCount
  vec2 vertexResolution = vec2(maxVerticesPerLine, floor(finalMaxVertexCount/maxVerticesPerLine));

  //we can now calculate the final number of elements
  float finalVertexCount = vertexResolution.x*vertexResolution.y;

  //and adjust finalVertexId
  float finalVertexId = mod(vertexId,finalVertexCount);

  //Calculate the position of the elements based on their finalVertexId
  //simfragCoord <=> gl_FragCoord in the vertex shader

  vec2 simfragCoord = vec2(mod(finalVertexId, vertexResolution.x),floor(finalVertexId / vertexResolution.x));

  //Step 3 >> calculate vertice positions
  //relative coordinate of the vertex (cordinates in 0..1 referential)
  float u = (simfragCoord.x /vertexResolution.x);
  float v = (simfragCoord.y /vertexResolution.y);

  //calculate coordinates in -1.>>1.0 space
  float sizeFactor = 1.;
  float fact = 2.*sizeFactor;
  float ux = fact*(u - 0.5);
  float vy = fact*(v - 0.5);

  gl_Position = vec4(ux, vy, 0., 1.);

  //gl_PointSize = ((resolution.y/numAcrossDown)*2.-1.) * 0.97;


  //logistic


  float snd = texture2D(sound, vec2(0.1,  ux)).a;
  float snd2 = texture2D(sound, vec2(0.1,  0.3)).a;
  //float snd3 = texture2D(sound, vec2(0.1,  0.5)).a;


  vec2 p = vec2(ux ,  vy);
  //float z = fract(0.86);
  float z = fract(0.9*snd*2.+cos(1./2.)/40.);
  int a = 0;
  float trig = (cos(2. * M_PI * z) + 1.) / 2.;


  if(p.x>0.)p.x = -p.x;

  if(p.y<0.)p.y = -p.y;

  float coeff = mix(1., 3.75, trig) + p.x * mix(3., 0.25, trig);


  for (int i = 0; i < ITERS; i++) {
    a += (z > p.y && z <= p.y + 1. / vertexResolution.y) ? 1 : 0;
    z = coeff * z * (1.-z);
  };

  float iters = float(ITERS);

  float g = 25. * float(a) / iters;


  gl_Position = vec4(ux, vy, 0, 1);

  //gl_PointSize = (resolution.y/vertexResolution.y) * 1.;//(resolution.y/numAcrossDown)*2.-1.;
 gl_PointSize = 2.*resolution.y/vertexResolution.y;

  v_color = vec4(g, g/8., g / 3.,1.);




  //v_color = vec4(1, 0, 0, 1);
}
