vec4 quad(int id) {
  if (id == 0) {
    return vec4(-1, 1, 0, 1);
  } else if (id == 1 || id == 4) {
    return vec4(-1, -1, 0, 1);
  } else if (id == 2 || id == 3) {
    return vec4(1, 1, 0, 1);
  } else if (id == 5) {
    return vec4(1, -1, 0, 1);
  } else {
  	return vec4(0);
  }
}

void scale(inout vec4 v, vec3 scale) {
	v.xyz *= scale;
}

void scale(inout float v, float scale) {
	v *= scale;
}

void trans(inout vec4 v, vec3 trans) {
	v.xyz += trans;
}

void trans(inout float v, float trans) {
	v += trans;
}

float duplicate(inout int id, int n) {
  if (id < n) {
  	return -1.;
  } else {
  	id -= n;
    return 1.;
  }
}

void main() {
  int id = int(vertexId);
  float side2 = duplicate(id, 12);
  float side = duplicate(id, 6);
  vec4 q = quad(id);
  scale(q, vec3(.3));
  trans(q.y, side * .4);
  trans(q.x, side2 * .4);
  trans(q.x, sin(time));
  scale(q.x, resolution.y / resolution.x);
  gl_Position = q;
  v_color = vec4(1);
}
